use std::convert::TryFrom;

use anyhow::Error;
use crypto::hash::ChainId;
use tezos_messages::p2p::{
    binary_message::{BinaryRead, BinaryWrite},
    encoding::prelude::*,
};

#[test]
fn can_deserialize_and_serialize_current_branch_message() {
    let data_list = [
        "",
    ];

    let branches: Vec<CurrentBranchMessage> = data_list
        .iter()
        .map(|d| hex::decode(d).unwrap())
        .map(|bytes| CurrentBranchMessage::from_bytes(bytes).unwrap())
        .collect();

    for (idx, branch) in branches.iter().enumerate() {
        let expected = data_list[idx];
        let actual = hex::encode(branch.as_bytes().unwrap());
        assert_eq!(expected, actual);
    }
}

#[test]
fn can_serialize_get_current_branch_message() {
    let get_current_branch_message = PeerMessage::GetCurrentBranch(GetCurrentBranchMessage::new(
        ChainId::try_from(hex::decode("8eceda2f").unwrap()).unwrap(),
    ));
    let response: PeerMessageResponse = get_current_branch_message.into();
    let message_bytes = response.as_bytes().unwrap();
    let expected_writer_result = hex::decode("0000000600108eceda2f").expect("Failed to decode");
    assert_eq!(expected_writer_result, message_bytes);
}

#[test]
fn can_deserialize_current_branch_message() -> Result<(), Error> {
    let message_bytes = hex::decode("")?;
    let message = PeerMessageResponse::from_bytes(message_bytes)?;
    let message = message.message();

    match message {
        PeerMessage::CurrentBranch(current_branch_message) => {
            assert_eq!(
                &hex::decode("8eceda2f")?,
                current_branch_message.chain_id().as_ref()
            );

            let current_head = current_branch_message.current_branch().current_head();
            assert_eq!(198_392, current_head.level());
            assert_eq!(4, current_head.validation_pass());

            assert_eq!(
                &hex::decode("46a6aefde9243ae18b191a8d010b7237d5130b3530ce5d1f60457411b2fa632d")?,
                current_head.predecessor().as_ref()
            );
            assert_eq!(
                &hex::decode("934484026d24be9ad40c98341c20e51092dd62bbf470bb9ff85061fa981ebbd9")?,
                current_head.context().as_ref()
            );
            assert_eq!(
                &hex::decode("acecbfac449678f1d68b90c7b7a86c9280fd373d872e072f3fb1b395681e7149")?,
                current_head.operations_hash().as_ref()
            );

            assert_eq!(2, current_head.fitness().len());
            let fitness_0 = current_head.fitness().get(0).unwrap();
            let expected_fitness_0 = hex::decode("00")?;
            assert_eq!(&expected_fitness_0, fitness_0);
            let fitness_1 = current_head.fitness().get(1).unwrap();
            let expected_fitness_1 = hex::decode("00000000005ba1ca")?;
            assert_eq!(&expected_fitness_1, fitness_1);

            assert_eq!(75, current_head.protocol_data().len());
            let expected_protocol_data = hex::decode("0000000000031b4f9aff00c6d9a5d1fbf5eda49a01e52017dc78ca1d7a45f3f4fe32840052f9845a61ccdd6cf20139cedef0ed52395a327ad13390d9e8c1e999339a24f8513fe513ed689a")?;
            assert_eq!(&expected_protocol_data, current_head.protocol_data());

            assert_eq!(141, current_branch_message.current_branch().history().len());

            let bytes = current_branch_message.as_bytes()?;
            // without length: 00001278 and without tag 0011
            Ok(assert_eq!(
                hex::decode("")?,
                bytes
            ))
        }
        _ => panic!("Unsupported encoding: {:?}", message),
    }
}
